import { useDrawer } from 'react-zen-ui';

export default function DrawerStateComponent(){
    const isDrawerOpen = useDrawer();
    return (
        <div>
            The drawer is currently {isDrawerOpen ? 'open' : 'closed'}.
        </div>
    );
}

import { useTheme } from 'react-zen-ui';

export default function ThemedComponent(){
    const theme = useTheme();
    return (
        <div style={{backgroundColor: theme.backgroundColor, color: theme.textColor}}>
            This component is themed!
        </div>
    );
}
